"use strict";
import { geometries } from "@jscad/modeling";
import { CsgGeometryType, CSG_GEOMETRY_TYPES_SET } from "./CsgCommon";
export function isCSGObject(o) {
  return CSG_GEOMETRY_TYPES_SET.has(o.type);
}
export function csgIsPath2(geometry) {
  return geometries.path2.isA(geometry);
}
export function csgIsGeom2(geometry) {
  return geometries.geom2.isA(geometry);
}
export function csgIsGeom3(geometry) {
  return geometries.geom3.isA(geometry);
}
export function csgGeometryTypeFromGeometry(geometry) {
  if (csgIsPath2(geometry)) {
    return CsgGeometryType.PATH2;
  }
  if (csgIsGeom2(geometry)) {
    return CsgGeometryType.GEOM2;
  }
  if (csgIsGeom3(geometry)) {
    return CsgGeometryType.GEOM3;
  }
  return CsgGeometryType.GEOM3;
}
