"use strict";
export const QUAD_HALF_EDGE_SIDES = ["s", "n", "w", "e"];
export const CCW_HALF_EDGE_SIDES = ["s", "e", "n", "w"];
export const NEIGHBOUR_INDICES = [0, 1, 2, 3];
export function quadHalfEdgeIndices(quad, edgeIndex, target) {
  target.index0 = quad[edgeIndex];
  target.index1 = quad[edgeIndex == 3 ? 0 : edgeIndex + 1];
}
