"use strict";
const EDGES = [
  [0, 1],
  [1, 2],
  [2, 0]
];
function sortPointIdPair(ids) {
  if (ids.id0 > ids.id1) {
    const temp = ids.id0;
    ids.id0 = ids.id1;
    ids.id1 = temp;
  }
  return ids;
}
export function edgeId(ids) {
  sortPointIdPair(ids);
  return `${ids.id0}-${ids.id1}`;
}
export function triangleEdge(triangle, edgeIndex) {
  const edgeIndices = EDGES[edgeIndex];
  return {
    id0: triangle[edgeIndices[0]],
    id1: triangle[edgeIndices[1]]
  };
}
