export default "// https://www.shadertoy.com/view/Xdlczl\n#define DRAG_MULT 0.048\n#define ITERATIONS_NORMAL 48\nvec2 wavedx(vec2 position, vec2 direction, float speed, float frequency, float timeshift) {\n	float x = dot(direction, position) * frequency + timeshift * speed;\n	float wave = exp(sin(x) - 1.0);\n	float dx = wave * cos(x);\n	return vec2(wave, -dx);\n}\nfloat getwaves(vec2 position, float currentTime, float freq, float freqMult, float speedMult){\n	float iter = 0.0;\n	float speed = 2.0;\n	float weight = 1.0;\n	float w = 0.0;\n	float ws = 0.0;\n	for(int i=0;i<ITERATIONS_NORMAL;i++){\n		vec2 p = vec2(sin(iter), cos(iter));\n		vec2 res = wavedx(position, p, speed, freq, currentTime);\n		position += p * res.y * weight * DRAG_MULT;\n		w += res.x * weight;\n		iter += 12.0;\n		ws += weight;\n		weight = mix(weight, 0.0, 0.2);\n		freq *= freqMult;\n		speed *= speedMult;\n	}\n	return (w / ws);\n}";
